mapper.select:
SELECT nb.*, cc.child_code_name AS categoryName
FROM notice_board AS nb
JOIN category_child_code AS cc ON nb.child_code_value = cc.child_code_value
WHERE 1=1
AND is_noticed = 0

    AND nb.child_code_value IN (
    SELECT cc.child_code_value FROM category_child_code AS cc WHERE cc.child_code_name = #{categoryName}
    )



LIMIT #{pageSize}
OFFSET #{offset}

mapper.update:
UPDATE notice_board
SET visit_count = visit_count + 1
WHERE board_id = #{boardId}

mapper.select:
SELECT fb.*, cc.child_code_name AS categoryName
FROM free_board AS fb
JOIN category_child_code AS cc ON fb.child_code_value = cc.child_code_value
WHERE 1=1

    AND fb.child_code_value IN (
    SELECT cc.child_code_value FROM category_child_code AS cc WHERE cc.child_code_name = #{categoryName}
    )



LIMIT #{pageSize}
OFFSET #{offset}

mapper.update:
UPDATE free_board
SET visit_count = visit_count + 1
WHERE board_id = #{boardId}
